import{_ as K,r,o as n,c as s,a as e,b as t,d as p,e as a}from"./app.98155842.js";const c={},O=p("\u6587\u7AE0\u5F15\u81EA "),_={href:"https://baijiahao.baidu.com/s?id=1719735633274806442&wfr=spider&for=pc",target:"_blank",rel:"noopener noreferrer"},R=p("OKR \u5230\u5E95\u662F\u4E2A\u5565\uFF1F\u8FD9\u91CC\u6709\u4E00\u4EFD\u901A\u4FD7\u89E3\u8BFB\uFF0C\u4E00\u770B\u5C31\u61C2"),i=p("\uFF0C\u4F5C\u8005\uFF1A"),l={href:"https://author.baidu.com/home?from=bjh_article&app_id=1634424735580176",target:"_blank",rel:"noopener noreferrer"},d=p("\u55BB\u5FB7\u6B66"),I=p("\uFF0C\u4FB5\u5220"),P=a("<p>OKR \u7684\u5E94\u7528\u8D8A\u6765\u8D8A\u5E7F\u6CDB\uFF0C\u4F46\u4E0D\u5C11\u4F01\u4E1A\u4ECD\u5728\u628A OKR \u5F53\u6210\u4E86 KPI \u5728\u7528\uFF0C\u5176\u5B9E\u4E8C\u8005\u660E\u663E\u4E0D\u540C\u3002</p><p>\u6211\u4E3E\u4E2A\u81EA\u8EAB\u7684\u4F8B\u5B50\uFF1A</p><p>\u6709\u4E00\u9635\u5B50\u6211\u4E0D\u660E\u539F\u56E0\u7684\u5934\u75BC\uFF0C\u4E25\u91CD\u5F71\u54CD\u5230\u6388\u8BFE\uFF0C\u4E8E\u662F\u6211\u53BB\u533B\u9662\u68C0\u67E5\uFF0C\u533B\u751F\u8BA9\u6211\u67E5\u67E5\u8840\u538B\uFF0C\u7ED3\u679C\u4E00\u770B\uFF0C\u6536\u7F29\u538B 145\uFF0C\u539F\u6765\u662F\u9AD8\u8840\u538B\u3002\u625B\u4E0D\u4F4F\u4E86\uFF0C\u53EA\u5F97\u5403\u836F\u3002</p><p>\u901A\u8FC7\u5403\u836F\uFF0C\u628A\u6536\u7F29\u538B\u63A7\u5236\u5728 140 \u4EE5\u5185\uFF0C\u8FD9\u5C31\u662F\u6211\u7684\u5173\u952E\u7EE9\u6548\u6307\u6807\uFF0C\u4E5F\u5C31\u662F KPI\u3002</p><p>\u4F46\u5403\u836F\u53EA\u80FD\u6CBB\u6807\uFF0C\u4E0D\u80FD\u6CBB\u672C\uFF0C\u800C\u4E14\u4E00\u65E6\u5403\u4E86\u9AD8\u8840\u538B\u5C31\u662F\u201C\u836F\u4E0D\u80FD\u505C\u201D\u3002</p><p>\u7A76\u7ADF\u662F\u4EC0\u4E48\u539F\u56E0\u4F1A\u5BFC\u81F4\u8840\u538B\u5347\u9AD8\u5462\uFF1F\u533B\u751F\u4F1A\u544A\u8BC9\u4F60\uFF0C\u8FD9\u662F\u539F\u53D1\u6027\u7684\uFF0C\u539F\u56E0\u5F88\u590D\u6742\uFF0C\u662F\u591A\u65B9\u9762\u7684\uFF0C\u5E76\u4E0D\u662F\u4E00\u4E00\u5BF9\u5E94\u7684\u56E0\u679C\u5173\u7CFB\u3002\u4F46\u53EF\u4EE5\u80AF\u5B9A\u7684\u662F\uFF0C\u9AD8\u8840\u538B\u4E0E\u4E00\u4E2A\u4EBA\u7684\u5E74\u9F84\u589E\u957F\u548C\u8EAB\u4F53\u80A5\u80D6\u6709\u5F88\u5927\u5173\u7CFB\uFF0C\u800C\u8FD9\uFF0C\u53C8\u4E0E\u4E00\u4E2A\u4EBA\u7684\u996E\u98DF\u7ED3\u6784\u548C\u71AC\u591C\u4E60\u60EF\u6709\u5173\u3002</p><p>\u6240\u4EE5\uFF0C\u5403\u836F\u63A7\u5236\u6307\u6807\u662F\u4E00\u4E2A\u65B9\u9762\uFF0C\u6700\u91CD\u8981\u7684\u662F\uFF1A\u6211\u8981\u51CF\u80A5\uFF0C\u4FDD\u6301\u8EAB\u4F53\u5065\u5EB7\u3002\u8FD9\u624D\u662F\u6211\u771F\u6B63\u7684\u76EE\u6807\uFF0C\u4E5F\u5C31\u662F OKR \u4E2D\u7684 O\uFF08\u76EE\u6807\uFF0Cobjective \uFF09\u3002</p><p>\u8BF4\u5230\u8FD9\u91CC\uFF0C\u53EF\u80FD\u4F60\u5C31\u660E\u767D\u4E86\u3002\u4EC0\u4E48\u53EB OKR\uFF1F\u5C31\u662F\u76EE\u6807\u4E0E\u5173\u952E\u6210\u679C\uFF08objective and key rusults\uFF09\uFF0C\u7B80\u5355\u6765\u8BF4\uFF0C\u5C31\u662F\u6574\u4E2A\u516C\u53F8\u3001\u56E2\u961F\u548C\u4E2A\u4EBA\uFF0C\u90FD\u8981\u8BBE\u7ACB\u76EE\u6807\uFF08objective \uFF09\u4EE5\u53CA\u8861\u91CF\u8FD9\u4E9B\u76EE\u6807\u5B8C\u6210\u4E0E\u5426\u7684\u5173\u952E\u7ED3\u679C\uFF08key rusults\uFF09\u3002</p><p>\u770B\u4E86\u8FD9\u4E2A\u5B9A\u4E49\uFF0C\u4F60\u5C31\u4F1A\u53D1\u73B0\uFF0COKR \u53EF\u4EE5\u5206\u6210\u4E24\u90E8\u5206\u6765\u7406\u89E3\uFF1A\u4E00\u4E2A\u662F O\uFF0C\u4E00\u4E2A\u662F KR\uFF0C\u76EE\u6807\u662F\u4E00\u5757\uFF0C\u5173\u952E\u7ED3\u679C\u662F\u4E00\u5757\u3002\u5173\u952E\u7ED3\u679C\u662F\u5BF9\u76EE\u6807\u7684\u91CF\u5316\u5206\u89E3\uFF0C\u628A\u4E8C\u8005\u5173\u8054\u8D77\u6765\uFF0C\u5C31\u5F62\u6210\u4E86 OKR\u3002</p><p>\u901A\u8FC7\u4E0A\u9762\u6211\u81EA\u8EAB\u9AD8\u8840\u538B\u7684\u4F8B\u5B50\uFF0C\u53EF\u80FD\u6709\u4EBA\u5DF2\u7ECF\u641E\u660E\u767D\u4E86 KPI \u4E0E OKR \u7684\u533A\u522B\u3002</p><p>\u5176\u5B9E\u8FD9\u4E8C\u8005\u7684\u533A\u522B\u8FD8\u53EF\u4EE5\u518D\u7B80\u5316\u4E00\u4E0B\uFF1AOKR \u5C31\u662F\u5B9A\u6027+\u5B9A\u91CF\uFF0CKPI \u53EA\u6709\u5B9A\u91CF\uFF0C\u6CA1\u6709\u5B9A\u6027\u3002</p><p>\u6211\u4EEC\u8FD8\u662F\u4E3E\u4E2A\u4F8B\u5B50\uFF1A</p><p>\u201C\u8FD9\u4E2A\u5B63\u5EA6\uFF0C\u6211\u4EEC\u7684\u76EE\u6807\u4E4B\u4E00\uFF0C\u662F\u8BBE\u8BA1\u4E00\u4E2A\u5438\u5F15\u773C\u7403\u7684\u7F51\u7AD9\uFF0C\u5F15\u8D77\u4F01\u4E1A\u7BA1\u7406\u8005\u5BF9 OKR \u7684\u5173\u6CE8\u3002\u201D</p><p>\u8FD9\u4E2A\u76EE\u6807\uFF08O\uFF09\u662F\u5B9A\u6027\u7684\uFF0C\u6CA1\u6709\u6570\u5B57\u3002</p><p>\u800C\u8981\u8FBE\u5230\u8FD9\u4E2A\u76EE\u6807\uFF0C\u6211\u4EEC\u5FC5\u987B\u8BBE\u5B9A\u76F8\u5E94\u7684\u201C\u5173\u952E\u7ED3\u679C\u201D\uFF0C\u5373\uFF1A</p><p>\u201C20%\u7684\u8BBF\u5BA2\u4E00\u5468\u5185\u4F1A\u518D\u6B21\u8BBF\u95EE\u672C\u7F51\u7AD9\u3002\u201D\u3001\u201C10%\u7684\u8BBF\u5BA2\u4F1A\u5BFB\u6C42\u6211\u4EEC\u7684\u57F9\u8BAD\u548C\u54A8\u8BE2\u670D\u52A1\u3002\u201D</p><p>\u5173\u952E\u7ED3\u679C\u662F\u6709\u6570\u5B57\u7684\uFF0C\u662F\u53EF\u91CF\u5316\u53EF\u6D4B\u91CF\u7684\u3002</p><p>\u53EA\u8981\u628A\u5173\u952E\u7ED3\u679C\uFF08KR\uFF09\u7684\u90E8\u5206\u53D8\u4E00\u4E0B\u8EAB\uFF0C\u5C31\u80FD\u5F62\u6210 KPI\u3002</p><p>\u4F8B\u5982\uFF0C\u6211\u4EEC\u628A\u201C10%\u7684\u8BBF\u5BA2\u4F1A\u5BFB\u6C42\u6211\u4EEC\u7684\u57F9\u8BAD\u548C\u54A8\u8BE2\u670D\u52A1\u201D\u53D8\u4E3A\u201C\u8BBF\u5BA2\u8F6C\u5316\u7387 \u226510%\u201D\uFF0C\u8FD9\u5C31\u662F\u4E00\u6761\u6807\u51C6\u7684 KPI\u3002</p><p>\u6362\u53E5\u8BDD\u8BF4\uFF0COKR \u6BD4 KPI \u591A\u4E86\u5B9A\u6027\u5316\u7684\u63CF\u8FF0\u3002</p><p>\u4E3A\u4EC0\u4E48\u76EE\u6807\u6709\u4E86\u5B9A\u91CF\u8FD8\u4E0D\u884C\uFF0C\u8FD8\u5F97\u6709\u5B9A\u6027\u5462\uFF1F</p><p>\u8FD9\u91CC\u5C31\u4E0D\u5F97\u4E0D\u8BF4\u4E00\u4E2A\u5178\u578B\u7684\u4F8B\u5B50\uFF1A\u67D0\u4E92\u8054\u7F51\u516C\u53F8\u4EE5\u524D\u5B9E\u65BD KPI \u8003\u6838\uFF0C\u4ED6\u4EEC\u8003\u6838\u7A0B\u5E8F\u5458\u7684 KPI \u6307\u6807\u662F\uFF1A\u6BCF\u5929\u5199\u4EE3\u7801\u6700\u5C11\u4E0D\u80FD\u4F4E\u4E8E\u591A\u5C11\u884C\uFF0C\u5DEE\u9519\u7387\u4E0D\u80FD\u9AD8\u4E8E\u591A\u5C11\u2026\u2026\u57FA\u672C\u4E0A\u90FD\u662F\u8FD9\u4E00\u7C7B\u5BF9\u5DE5\u4F5C\u91CF\u548C\u5DEE\u9519\u7387\u7684\u8003\u6838\u3002</p><p>\u770B\u4E0A\u53BB\u8FD9\u4E2A\u8003\u6838\u65E2\u8003\u8651\u4E86\u5DE5\u4F5C\u6570\u91CF\uFF0C\u53C8\u8003\u8651\u5230\u5DE5\u4F5C\u8D28\u91CF\uFF0C\u5F88\u5B8C\u5584\u3002\u4E8B\u5B9E\u4E0A\u8FD9\u6837\u7684\u8003\u6838\u4E25\u91CD\u5236\u7EA6\u4E86\u7A0B\u5E8F\u5458\u53D1\u6325\u4E3B\u89C2\u80FD\u52A8\u6027\u3002\u8BA9\u4E00\u4E9B\u7A0B\u5E8F\u5458\u53F9\u606F\uFF1A</p><p>\u201C\u81EA\u4ECE\u6709\u4E86\u8003\u6838\uFF0C\u6211\u53EA\u80FD\u9762\u5411 KPI \u7F16\u7A0B\uFF0C\u800C\u4E0D\u662F\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u3002\u201D</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0CKPI \u91CF\u5316\u7684\u80CC\u540E\uFF0C\u4E00\u822C\u662F\u8DDF\u8003\u6838\u7ED3\u679C\u76F4\u63A5\u6302\u94A9\uFF0C\u5F04\u4E0D\u597D\u8FD8\u4F1A\u88AB\u6263\u5206\uFF0C\u8FD9\u65E0\u7591\u662F\u628A KPI \u7684\u5F0A\u7AEF\u8FDB\u4E00\u6B65\u653E\u5927\u4E86\u3002</p><p>\u6211\u4EEC\u8981\u660E\u767D\uFF0C\u771F\u6B63\u7684\u7F16\u7A0B\u9AD8\u624B\uFF0C\u4ED6\u662F\u5C3D\u53EF\u80FD\u7528\u5C11\u91CF\u7684\u4EE3\u7801\u89E3\u51B3\u9AD8\u96BE\u5EA6\u7684\u95EE\u9898\uFF0C\u8FD9\u624D\u662F\u6211\u4EEC\u9700\u8981\u7684\u4EBA\u624D\uFF0C\u800C\u4E0D\u662F\u5236\u9020\u590D\u6742\uFF0C\u9020\u6210\u7CFB\u7EDF\u5197\u4F59\u548C\u9AD8\u6602\u7684\u4EBA\u5DE5\u6210\u672C\uFF08\u7A0B\u5E8F\u5458\u7684\u5DE5\u8D44\u53EF\u4E0D\u4F4E\uFF0C\u54EA\u80FD\u8FD9\u6837\u6D6A\u8D39\uFF09\u3002</p><p>\u7C7B\u4F3C\u7684\uFF0C\u7528 KPI \u8003\u6838\uFF0C\u8FD8\u6709\u89E3\u51B3\u6295\u8BC9\u7387\u8FD9\u6837\u7684\u6307\u6807\uFF0C\u89E3\u51B3\u6295\u8BC9\u7387\u8D8A\u9AD8\uFF0C\u8BF4\u660E\u4ED6\u5DE5\u4F5C\u8D8A\u597D\uFF0C\u4F46\u6070\u6070\u8BF4\u660E\u4E86\u516C\u53F8\u63D0\u4F9B\u7684\u4EA7\u54C1\u548C\u670D\u52A1\u6709\u591A\u70C2\uFF0C\u6BCF\u5929\u90FD\u5728\u53D7\u7406\u548C\u89E3\u51B3\u6295\u8BC9\u3002\u7ED3\u679C\u5C31\u51FA\u73B0\u4E86\u89E3\u51B3\u6295\u8BC9\u7684\u4EBA\u8003\u6838\u5F97\u5206\u5F88\u9AD8\u4F46\u516C\u53F8\u5F97\u5206\u5F88\u4F4E\u8FD9\u79CD\u5F88\u53CD\u5E38\u7684\u73B0\u8C61\u3002</p><p>\u6B63\u5982\u6D77\u5E95\u635E\u7684 CEO \u5F20\u52C7\u81EA\u6211\u53CD\u601D\u7684\u90A3\u6837\uFF0C\u4ED6\u8BF4\uFF0C\u6BCF\u4E00\u4E2A KPI \u4F53\u7CFB\u80CC\u540E\u90FD\u9690\u85CF\u7740\u4E00\u4E2A\u590D\u4EC7\u5973\u795E\u3002</p><p>\u4E3A\u4E86\u63D0\u9AD8\u7528\u6237\u6EE1\u610F\u5EA6\uFF0C\u53EA\u8981\u662F\u6765\u5403\u706B\u9505\u7684\u6234\u773C\u955C\u7684\u5BA2\u4EBA\uFF0C\u90FD\u8981\u7ED9\u4ED6\u4E00\u5757\u773C\u955C\u5E03\uFF1B\u676F\u5B50\u91CC\u7684\u996E\u6599\u4F4E\u4E8E 1/3\uFF0C\u5C31\u8981\u8D76\u7D27\u7ED9\u5BA2\u4EBA\u52A0\u996E\u6599\uFF1B\u5982\u679C\u5BA2\u4EBA\u5E26\u4E86\u624B\u673A\uFF0C\u628A\u624B\u673A\u653E\u5728\u684C\u4E0A\uFF0C\u8981\u8D76\u7D27\u7528\u4E00\u4E2A\u5851\u6599\u888B\u628A\u5B83\u7ED9\u5957\u4E0A\u3002\u5982\u679C\u505A\u4E0D\u5230\uFF0C\u5C31\u6263 0.05 \u5206\uFF0C\u6700\u540E\u76F4\u63A5\u53CD\u6620\u5728\u5DE5\u8D44\u7EE9\u6548\u4E0A\u3002</p><p>\u6240\u4EE5\uFF0C\u8FD9\u4E9B\u5E97\u5458\u53EA\u80FD\u88AB\u8FEB\u201C\u9A9A\u6270\u201D\u987E\u5BA2\u3002\u987E\u5BA2\u4E0D\u8981\u996E\u6599\uFF0C\u4ED6\u4EEC\u8FD8\u8981\u5F80\u91CC\u5934\u52A0\u3002\u987E\u5BA2\u4E0D\u559C\u6B22\u7528\u5851\u6599\u888B\u628A\u624B\u673A\u5957\u4E0A\u3002\u4F46\u4E0D\u884C\uFF0C\u56E0\u4E3A\u5E97\u5458\u8003\u6838\u7684\u662F KPI\uFF0C\u4E00\u65E6\u88AB\u5E97\u957F\u53D1\u73B0\uFF0C\u5C31\u4F1A\u88AB\u6263\u5DE5\u8D44\u3002</p><p>\u770B\u5427\uFF0C\u8FD9\u5C31\u53CD\u5E94\u4E86 KPI \u7684\u5F0A\u7AEF\uFF0C\u6709\u65F6\u5019\u662F\u4EE5\u6253\u6270\u987E\u5BA2\u3001\u8BA9\u987E\u5BA2\u4F53\u9A8C\u66F4\u5DEE\u4E3A\u4EE3\u4EF7\u7684\u3002</p><p>\u5982\u679C\u6211\u4EEC\u7684\u8003\u6838\u5FD8\u8BB0\u4E86\u76EE\u6807\u662F\u4EC0\u4E48\uFF0C\u5C31\u5FD8\u8BB0\u4E86\u4E3A\u4EC0\u4E48\u800C\u51FA\u53D1\uFF0C\u90A3\u6211\u4EEC\u5C31\u5F88\u5BB9\u6613\u53D8\u6210\u4E00\u4E2A\u76F2\u76EE\u52E4\u52B3\u7684\u4EBA\u3002</p><p>KPI \u662F\u8861\u91CF\u3001\u6D4B\u91CF\u5DE5\u5177\uFF0C\u7C7B\u4F3C\u4E8E\u79D2\u8868\uFF0C\u4F46\u662F\u4E0D\u5C11\u4EBA\u7528\u7740\u7528\u7740\uFF0C\u5C31\u628A\u5B83\u5F53\u6210\u6307\u5357\u9488\u4E86\uFF0C\u628A\u5DE5\u4F5C\u8981\u6C42\u5F53\u505A\u76EE\u7684\u672C\u8EAB\u4E86\u3002</p><p>\u6362\u53E5\u8BDD\u8BF4\uFF0CKPI \u7684\u4E3B\u8981\u529F\u80FD\u662F\u4E3A\u4E86\u907F\u514D\u51FA\u9519\uFF0C\u800C\u4E0D\u662F\u786E\u4FDD\u6210\u529F\uFF0C\u56E0\u4E3A\u5B83\u662F\u81EA\u6211\u89C6\u89D2\uFF0C\u4E0D\u662F\u5BA2\u6237\u89C6\u89D2\uFF0C\u5931\u53BB\u4E86\u76EE\u6807\u5BFC\u5411\uFF0C\u4E5F\u5C31\u5931\u53BB\u4E86\u65B9\u5411\u7684\u6307\u5F15\u3002</p><p>\u6240\u4EE5\uFF0COKR \u662F\u5728 KPI \u5B9A\u91CF\u7684\u57FA\u7840\u4E0A\uFF0C\u589E\u52A0\u5B9A\u6027\u903B\u8F91\u3002</p><p>\u76EE\u6807\u6709\u4E86\u5B9A\u6027\uFF0C\u624D\u80FD\u53CD\u6620\u51FA\u201D\u6211\u60F3\u505A\u4EC0\u4E48\u201D\uFF0C\u518D\u52A0\u4E0A\u76EE\u7684\u548C\u65F6\u9650\u8981\u6C42\uFF0C\u5C31\u80FD\u591F\u5236\u5B9A\u51FA\u5177\u6709\u6311\u6218\u6027\u7684\u76EE\u6807\uFF0C\u4ECE\u800C\u9F13\u821E\u4EBA\u5FC3\u3001\u6FC0\u53D1\u56E2\u961F\u5171\u9E23\u3002</p><p>\u800C\u8981\u4FDD\u6301 OKR \u7684\u6311\u6218\u6027\uFF0C\u5C31\u4E0D\u80FD\u76F4\u63A5\u4E0E\u8003\u6838\u7ED3\u679C\u6302\u94A9\u3002\u7136\u800C\uFF0C\u4ECD\u7136\u6709\u4E00\u5C0F\u90E8\u5206\u4F01\u4E1A\uFF0C\u8FD8\u662F\u4E0E\u8003\u6838\u7ED3\u679C\u6302\u94A9\u3002\u6709 9%\u7684\u4F01\u4E1A\u5C06 OKR \u548C\u7EE9\u6548\u6302\u94A9\uFF0C\u76F4\u63A5\u8003\u6838 OKR \u7684\u8FBE\u6210\u7387\u3002\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0COKR \u6210\u4E86\u53E6\u4E00\u79CD\u5F62\u5F0F\u7684 KPI\uFF0C\u53EF\u4EE5\u8BF4\u53D8\u5473\u4E86\u3002</p><p>\u4E3A\u4EC0\u4E48\u6211\u4E0D\u4E3B\u5F20 OKR \u4E0E\u8003\u6838\u7ED3\u679C\u6302\u94A9\u5462\uFF1F\u539F\u56E0\u5728\u4E8E\uFF1A</p><p>\u4E00\u65E6\u7528 OKR \u8003\u6838\u5458\u5DE5\uFF0C\u90A3\u4E48\u5458\u5DE5\u4E0E\u516C\u53F8\u4E4B\u95F4\uFF0C\u4E0A\u4E0B\u7EA7\u4E4B\u95F4\uFF0C\u5BF9\u4E8E\u76EE\u6807\u8BBE\u5B9A\u5C31\u5F00\u59CB\u535A\u5F08\u4E86\uFF1A\u5458\u5DE5\u5E0C\u671B\u628A\u76EE\u6807\u5C3D\u91CF\u5B9A\u4F4E\uFF0C\u8FD9\u6837\u5C31\u66F4\u5BB9\u6613\u901A\u8FC7\u8003\u6838\uFF1B\u800C\u4F5C\u4E3A\u516C\u53F8\uFF0C\u5E0C\u671B\u628A\u76EE\u6807\u5C3D\u91CF\u5B9A\u9AD8\uFF0C\u8FD9\u6837\u516C\u53F8\u624D\u80FD\u53D1\u5C55\u5FEB\u3001\u4E1A\u7EE9\u66F4\u9AD8\u3002</p><p>OKR \u4F5C\u4E3A\u4E00\u5957\u76EE\u6807\u7BA1\u7406\u5DE5\u5177\uFF0C\u5E2E\u52A9\u4F01\u4E1A\u89E3\u51B3\u7684\u662F\u201C\u4EF7\u503C\u521B\u9020\u201D\u90E8\u5206\uFF0C\u662F\u201C\u4EF7\u503C\u8BC4\u4EF7\u4E0E\u5206\u914D\u201D\u4E4B\u524D\u7684\u73AF\u8282\u3002\u8FD9\u662F OKR \u4E0E\u7EE9\u6548\u8131\u94A9\u7684\u7406\u8BBA\u57FA\u7840\u3002</p><p>\u4F46\u662F\uFF0C\u95EE\u9898\u6765\u4E86\uFF0C\u5982\u679C OKR \u4E0D\u4E0E\u5458\u5DE5\u7EE9\u6548\u6302\u94A9\uFF0C\u90A3\u5927\u5BB6\u90FD\u4E0D\u5B8C\u6210\u5DE5\u4F5C\u7684\u8BDD\uFF0C\u662F\u4E0D\u662F\u5C31\u6CA1\u6709\u4EFB\u4F55\u529E\u6CD5\u7EA6\u675F\u4E86\uFF1F\u6BD5\u7ADF\u6211\u4EEC\u4E5F\u8981\u8003\u8651\u5230\uFF0C\u4EBA\u6709\u521B\u9020\u529B\u3001\u6709\u52C7\u4E8E\u6311\u6218\u6027\u7684\u4E00\u9762\uFF0C\u4F46\u4E5F\u6709\u61D2\u60F0\u3001\u61C8\u6020\u7684\u4E00\u9762\uFF0C\u5982\u679C\u6CA1\u6709\u8003\u6838\u7EA6\u675F\uFF0C\u4F1A\u4E0D\u4F1A\u5931\u63A7\u5462\uFF1F</p><p>\u8FD9\u4E2A\u95EE\u9898\u63D0\u5F97\u5F88\u597D\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u6211\u8BF4 OKR \u4E0D\u8981\u4E0E\u8003\u6838\u7ED3\u679C\u76F4\u63A5\u6302\u94A9\uFF0C\u4F46\u5E76\u4E0D\u4EE3\u8868\u4E00\u70B9\u4E5F\u4E0D\u6302\u94A9\uFF0C\u90A3\u5C31\u8D70\u5411\u4E86\u53E6\u4E00\u4E2A\u6781\u7AEF\u3002</p><p>\u6839\u636E\u5FAE\u8F6F\u3001\u8C37\u6B4C\u516C\u53F8\u7B49\u6210\u529F\u5E94\u7528 OKR \u7684\u7ECF\u9A8C\u6765\u770B\uFF0C\u786E\u5B9E\u4E0D\u8BA9 OKR \u4E0E\u8003\u6838\u7ED3\u679C\u6302\u94A9\uFF0C\u4F46\u4ED6\u4EEC\u4F1A\u7528 360 \u5EA6\u8003\u8BC4\u4F5C\u4E3A\u7EE9\u6548\u8BC4\u4F30\u7684\u91CD\u8981\u624B\u6BB5\uFF0C\u5F53\u4E0A\u7EA7\u4E3B\u7BA1\u62FF\u5230\u4E0B\u5C5E\u7684 360 \u5EA6\u8003\u8BC4\u7ED3\u679C\u540E\uFF0C\u4F1A\u7ED3\u5408\u8BE5\u5458\u5DE5\u7684 OKR \u8868\u73B0\uFF0C\u7ED9\u51FA\u4E00\u4E2A\u7EFC\u5408\u6027\u7684\u8BC4\u4F30\uFF0C\u7136\u540E\u6765\u8BBA\u529F\u884C\u8D4F\u3002</p><p>\u6240\u4EE5\u5343\u4E07\u4E0D\u8981\u8BA4\u4E3A OKR \u5BF9\u8003\u6838\u7ED3\u679C\u6CA1\u7528\uFF0C\u800C\u662F\u4E0D\u76F4\u63A5\u76F8\u5173\uFF0C\u4F46\u4ECD\u7136\u76F8\u5F53\u91CD\u8981\u3002</p><p>OKR \u9664\u4E86\u4E0D\u4E0E\u8003\u6838\u7ED3\u679C\u76F4\u63A5\u6302\u94A9\u8FD9\u4E2A\u7279\u70B9\u4E4B\u5916\uFF0C\u8FD8\u6709\u516C\u5F00\u900F\u660E\u3001\u81EA\u4E0B\u800C\u4E0A\u5236\u5B9A\u4E00\u90E8\u5206\u76EE\u6807\u7684\u7279\u70B9\uFF0C\u8FD9\u6837\u505A\u6709\u8FD9\u6837\u51E0\u4E2A\u597D\u5904\uFF1A</p><p>\u7B2C\u4E00\u3001\u65B9\u5411\u4E00\u81F4\u3002\u76EE\u6807\u516C\u5F00\u90FD\u80FD\u770B\u89C1\uFF0C\u90FD\u77E5\u9053\u5F7C\u6B64\u5E72\u4EC0\u4E48\uFF0C\u8FD9\u6837\u5C31\u80FD\u786E\u4FDD\u65B9\u5411\u4E00\u81F4\uFF0C\u9632\u6B62\u4E2D\u9014\u8D70\u504F\u3002</p><p>\u7B2C\u4E8C\u3001\u9AD8\u6548\u534F\u540C\u3002\u80FD\u6FC0\u53D1\u5458\u5DE5\u81EA\u4E3B\u5236\u5B9A\u76EE\u6807\u7684\u79EF\u6781\u6027\uFF0C\u540C\u65F6\u53C8\u80FD\u4FDD\u6301\u4E0A\u4E0B\u5DE6\u53F3\u76EE\u6807\u5BF9\u9F50\uFF0C\u80FD\u591F\u505A\u5230\u5B9E\u65F6\u53CD\u9988\uFF0C\u6B65\u8C03\u4E00\u81F4\uFF0C\u5F62\u6210\u5408\u529B\uFF0C\u9632\u6B62\u5404\u884C\u5176\u662F\u3002</p><p>\u7B2C\u4E09\u3001\u538B\u529B\u5012\u903C\u3002\u76EE\u6807\u548C\u7ED3\u679C\u4E00\u65E6\u516C\u5F00\uFF0C\u5C31\u4F1A\u5BF9\u76EE\u6807\u8FC7\u4F4E\u3001\u7ED3\u679C\u8FC7\u5DEE\u7684\u5458\u5DE5\u5F62\u6210\u65E0\u5F62\u538B\u529B\uFF0C\u8BA9\u4ED6\u8C03\u6574\u76EE\u6807\uFF0C\u52AA\u529B\u6539\u5584\u81EA\u8EAB\u7684\u7EE9\u6548\u3002</p><p>\u5F53\u7136\uFF0C\u60F3\u8BA9 OKR \u843D\u5730\uFF0C\u8D77\u5230\u5F88\u597D\u7684\u6548\u679C\uFF0C\u9664\u4E86\u589E\u52A0\u8BA4\u77E5\u4EE5\u5916\uFF0C\u8FD8\u9700\u8981\u62E5\u6709\u8D81\u624B\u7684\u5DE5\u5177\uFF0C\u90A3\u6837\u5C31\u4F1A\u8FBE\u5230\u4E8B\u534A\u529F\u500D\u7684\u6548\u679C\u3002\u6240\u8C13\u5DE5\u6B32\u5584\u5176\u4E8B\uFF0C\u5FC5\u5148\u5229\u5176\u5668\u3002\u5C31\u662F\u8FD9\u4E2A\u9053\u7406\u3002</p><p>\u6211\u4F5C\u4E3A\u7BA1\u7406\u987E\u95EE\uFF0C\u6DF1\u77E5\u62E5\u6709\u65B9\u6CD5\u548C\u5DE5\u5177\u7684\u91CD\u8981\u6027\u3002</p><p>\u60F3\u5F88\u597D\u7684\u5E94\u7528 OKR\uFF0C\u6211\u5E38\u5E38\u9F13\u52B1\u6211\u6240\u670D\u52A1\u7684\u4F01\u4E1A\uFF0C\u5E94\u8BE5\u5728\u4E1A\u52A1\u5355\u5143/\u56E2\u961F\u5C42\u9762\uFF0C\u6216\u8005\u5355\u4E2A\u9879\u76EE\u4E0A\u5148\u8BD5\u70B9\uFF0C\u4F46\u4E0D\u8981\u6025\u4E8E\u6C42\u6210\u3002</p><p>\u8FD9\u6837\u80FD\u6709\u6548\u964D\u4F4E\u63A8\u884C\u98CE\u9669\uFF0C\u51CF\u5C11\u8BD5\u9519\u6210\u672C\uFF0C\u901A\u8FC7\u5FEB\u901F\u5EFA\u7ACB OKR \u901F\u8D62\u9879\uFF0C\u7ED9\u5927\u5BB6\u770B\u5230\u5E0C\u671B\u5E76\u4E14\u5C1D\u5230\u751C\u5934\uFF0C\u800C\u540E\u5C31\u80FD\u83B7\u5F97\u66F4\u5E7F\u6CDB\u7684\u5173\u6CE8\u548C\u652F\u6301\uFF0C\u4ECE\u800C\u5F15\u8D77\u4E89\u76F8\u6548\u4EFF\uFF0C\u8FD9\u65F6\u5019\u518D\u5168\u9762\u63A8\u5E7F\u5C31\u6C34\u5230\u6E20\u6210\u4E86\u3002</p>",53);function h(u,f){const o=r("ExternalLinkIcon");return n(),s("div",null,[e("blockquote",null,[e("p",null,[O,e("a",_,[R,t(o)]),i,e("a",l,[d,t(o)]),I])]),P])}var b=K(c,[["render",h],["__file","okr.html.vue"]]);export{b as default};
